//Navbar Scrolling Effect
window.onscroll = function () {
  scrollFunction()
};

function scrollFunction() {
  var width = $(window).width();

  if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
    $('#nav').css('background','#fff');
    $('#nav').css('boxShadow','rgba(0, 0, 0, 0.2) 0px 0px 10px');
    
    
    if(width < 800) {
      $('.nav-link > a').css('color','#000');
    } else {
      $('.nav-link > a').css('color','#000');
      $('.line-bottom').css('borderBottom','2px solid #FF2E67');
    }
  } else {
    $('#nav').css('background','transparent');
    $('#nav').css('boxShadow','none');

    if(width < 800) {
      $('.nav-link > a').css('color','#000');
      $('.line-bottom').css('borderBottom','0');
    } else {
      $('.nav-link > a').css('color','#fff');
      $('.line-bottom').css('borderBottom','2px solid #fff');
    }
  }
}